const header=document.querySelector("header"),hamburger=document.querySelector(".hamburger"),navList=document.querySelectorAll(".nav-list");hamburger.addEventListener("click",(()=>{header.classList.toggle("active")})),navList.forEach((e=>{e.addEventListener("click",(()=>{header.classList.remove("active")}))}));const footerYear=document.querySelector(".year"),year=(new Date).getFullYear();footerYear.textContent=year;const fields=document.querySelectorAll(".contacts-wrapper > div > *");fields.forEach((e=>{e.addEventListener("click",(e=>{fields.forEach((e=>{e.classList.remove("active"),console.log(e.classList)})),e.target.classList.contains("active")||(e.target.classList.add("active"),console.log(e.target.classList))}))}));